import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { fetchCount } from "./counterAPI";

const initialState = {
  comicsList: [],
  value: 2,
  selectedComic: {},
};

export const comicSlice = createSlice({
  name: "comic",
  initialState,
  reducers: {
    increment: (state) => {
      state.value += 1;
    },

    decrement: (state) => {
      state.value -= 1;
    },
    incrementByAmount: (state, action) => {
      state.value += action.payload;
    },
    setList: (state, action) => {
      state.comicsList = action.payload;
    },
    setSelectedComic: (state, action) => {
      state.selectedComic = action.payload;
    },
  },
});

export const {
  increment,
  decrement,
  incrementByAmount,
  setList,
  setSelectedComic,
} = comicSlice.actions;

export const selectCount = (state) => state.comic.comicsList;
export const selectedComic = (state) => state.comic.selectedComic;

export default comicSlice.reducer;
