import { Light, Dark } from "./theme";

function App() {
  const [currentTheme, setCurrentTheme] = useState(Dark);
  return (
    <ThemeProvider theme={currentTheme}>
      <div className="App">
        {/* <Counter /> */}
        <Login />
      </div>
    </ThemeProvider>
  );
}

export default App;
