import styled from "styled-components";

export const Container = styled.div`
  height: 100%;
  background: ${(props) => props.theme.colors.primary};
  width: 100%;
  align-items: center;
  justify-content: center;
  display: flex;
  flex-direction: column;
`;

export const GitHubButton = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  height: 70px;
  width: 300px;
  background: #fff;
  border-radius: 15px;
  outline: none;
  border-width: 0px;
  align-items: center;
  transition: 0.2s;

  &:hover {
    opacity: 0.8;
    cursor: pointer;
    background: red;
  }

  .ghLink {
  }
`;

export const GHLink = styled.span`
  color: ${(props) => props.theme.colors.primary};
  text-decoration: none;
  font-size: 16px;
`;
