import React from "react";
import { Wrapper } from "./styles";
import { Link } from "react-router-dom";

const ComicCard = ({ uri, title }) => {
  return (
    <Wrapper>
      <Link to="/detail" style={{ flex: 1 }}>
        <img src={uri} />
        <span>{title}</span>
      </Link>
    </Wrapper>
  );
};
export default ComicCard;
